import requestsimport jsontoken = ''#need flask-ask set up#invoke all methods after launch() via intentswith open('config.json','r') as f:	config = json.load(f)def launch():	global token	login_url = config['base_url'] + "/login"	login_data = config['alarmbuddy_account']	x = requests.post(login_url, data = login_data)	if x.status_code != 200:		print("Status: ", x.status_code, " problem with request, Exiting.")	token = x.json()['token']def getFriends():	friendList_Url = config['base_url'] + '/friendsWith/' + config['alarmbuddy_account']['username']	response = requests.get(friendList_Url, headers = {'Authorization' : token})	data = response.json()	print("Your friends: ") #speakoutput	for value in data:		print(value['username2']) #speakoutputdef getIncomingFriendRequests():	friendRequest_url = config['base_url'] + '/requests/' + config['alarmbuddy_account']['username']	response = requests.get(friendRequest_url, headers = {'Authorization' : token})	data = response.json()	print("You have _ new friend requests from:") #speakoutput	for value in data:		print(value['senderUsername'])	#iterate through the entire list -- can we send any intent to another intent	#accept?		#acceptFriend()	#decline?		#denyFriend()def acceptFriend():	#maybe iterate through friend requests in same intent?	sender = '' #speakinput	acceptRequest_url = config['base_url'] + '/acceptFriendRequest/' + config['alarmbuddy_account']['username'] + '/' + sender	response = requests.post(acceptRequest_url, headers = {'Authorization' : token})	#speakoutput if successfuldef denyFriend():	sender = 'test' #speakinput	denyRequest_url = config['base_url'] + '/denyFriendRequest/' + config['alarmbuddy_account']['username'] + '/' + sender	response = requests.post(denyRequest_url, headers = {'Authorization' : token})	#speakoutput if successfuldef sendFriendRequest():	#who would you like to send to (speakoutput)	reciever = '' #speakinput	sendRequest_url = config['base_url'] + '/sendRequest/' + config['alarmbuddy_account']['username'] +'/' + reciever	response = requests.post(sendRequest_url, headers = {'Authorization' : token})	#print("Friend request sent")#speakoutput if successfuldef cancelFriendRequest():	reciever = '' #speakinput	#why is this the opposite of all other API requests. Fuck you DB.	cancelRequest_url = config['base_url'] + '/cancelFriendRequest/' + config['alarmbuddy_account']['username'] +'/' + reciever	response = requests.post(cancelRequest_url, headers = {'Authorization' : token})def deleteFriend():	friend = 'cowmoomoo' #speakinput	#deleteFriend does not work on DB end	deleteFriend_url = config['base_url'] +	'/deleteFriend/' + friend + '/' + config['alarmbuddy_account']['username']	#deleteFriend_url = config['base_url']+ '/deleteFriend/' + config['alarmbuddy_account']['username'] + '/' + friend	response = requests.post(deleteFriend_url, headers = {'Authorization' : token})	launch()#getFriends()#getIncomingFriendRequests()#sendFriendRequest()#acceptFriend()#denyFriend()#cancelFriendRequest()#deleteFriend()